import React, { useState } from 'react';
import './nav.css';
import { keywords } from './seo';

function Nav() {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const wrapper = document.getElementById("search-wrapper");
  
  const changeInput = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    // Filter keywords based on input
    const filteredSuggestions = keywords.filter(keyword =>
      keyword.toLowerCase().includes(value.toLowerCase())
    );

    setSuggestions(filteredSuggestions);

    if (wrapper) {
      wrapper.classList.add("active");
    }
  };

  const suggestionSelected = (value) => {
    setSearchTerm(value);
    setSuggestions([]);
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <a className="navbar-brand" href="#" id="brand">
        <img src="logo192.png" style={{ height: "30px", width: "30px" }} alt="React" /> React
      </a>
      <button
        className="navbar-toggler"
        type="button"
        data-bs-toggle="collapse"
        data-bs-target="#navbarNav"
        aria-controls="navbarNav"
        aria-expanded="false"
        aria-label="Toggle navigation"
      >
        <span className="navbar-toggler-icon"></span>
      </button>
      <div className="collapse navbar-collapse" id="navbarNav">
        <ul className="navbar-nav">
          <li className="nav-item">
            <a className="nav-link active" id="active" aria-current="page" href="#">
              Home
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              About
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              Contact
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              More
            </a>
          </li>
        </ul>

        <div className="search-wrapper" id="search-wrapper">
          <div className="input-group search-field">
            <input
              type="search"
              className="form-control form-control-sm"
              id="search"
              placeholder="Search any product you want!"
              value={searchTerm}
              onChange={changeInput}
            />
            {suggestions.length > 0 && (
              <div className="auto-com" id="autocom">
                {suggestions.map((suggestion, index) => (
                  <div key={index} onClick={() => suggestionSelected(suggestion)}>
                    {suggestion}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        <button
          className="btn btn-outline-secondary bg-primary"
          type="button"
          style={{ color: "#333333" }}
          id="searchbtn"
        >
          Search
        </button>

        <div className="cta">
          <button className="btn btn-primary" id="login">
            Login
          </button>
          <button className="btn btn-success" id="signup">
            Sign Up
          </button>
        </div>
      </div>
    </nav>
  );
}

export default Nav;
