import React, { useState } from 'react';

function Nav() {
  const [currentPage, setCurrentPage] = useState('home'); // Initially set to 'home'

  const handleNavItemClick = (page) => {
    setCurrentPage(page);
  };

  return (
    <nav>
      <ul id='navul'>
        <li className="navlist">
          <a onClick={() => handleNavItemClick('home')}>home</a>
        </li>
        <li className={`navlist ${currentPage === 'about' ? 'current' : ''}`}>
          <a onClick={() => handleNavItemClick('about')}>about</a>
        </li>
        <li className={`navlist ${currentPage === 'contact' ? 'current' : ''}`}>
          <a onClick={() => handleNavItemClick('contact')}>contact</a>
        </li>
        <li className={`navlist ${currentPage === 'more' ? 'current' : ''}`}>
          <a onClick={() => handleNavItemClick('more')}>more</a>
        </li>
      </ul>
    </nav>
  );
}

export default Nav;
