import React, { useState, useEffect } from "react";
import Nav from "./navBar/nav";
import Main from "./main/main";
import AboutPage from "./navigations/about";
import ContactPage from "./navigations/contactPage";
import Listing from "./product listing/listing";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { useLocation } from "react-router-dom";

export default function App() {
  const [category, setCategory] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");

  // Use the 'useLocation' hook to get the current route location
  const location = useLocation();

  useEffect(() => {
    // Check if the route contains the 'category' parameter
    const routeCategory = location.pathname.split("/")[2];
    
    if (routeCategory && routeCategory !== category) {
      // Set the category based on the route parameter
      setCategory(routeCategory);
      setSelectedCategory(routeCategory);
    }
  }, [location, category]);

  // Save the selected category to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem("selectedCategory", selectedCategory);
  }, [selectedCategory]);

  // Load the selected category from localStorage when the app starts
  useEffect(() => {
    const storedCategory = localStorage.getItem("selectedCategory");
    if (storedCategory) {
      setSelectedCategory(storedCategory);
    }
  }, []);

  return (
  <>
      <Nav />
      <Routes>
        <Route path="/about" element={<AboutPage />} />
        <Route path="/" element={<Main className="bg" set={setSelectedCategory} set2={category} set3={setCategory} />} />
        <Route path="/contact" element={<ContactPage />} />
        <Route path={`/${category}`} element={<Listing selectedCategory={selectedCategory} />} />
      </Routes>
  </>
  );
}
