import React, { useState } from 'react';
import './nav.css'; // You can style this as per your needs

const Nav = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const keywords = ['apple', 'banana', 'cherry', 'date', 'elderberry', 'fig', 'grape', 'honeydew', 'kiwi', 'lemon'];

  const onSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    const filteredSuggestions = keywords.filter(keyword =>
      keyword.toLowerCase().includes(value.toLowerCase())
    );

    setSuggestions(filteredSuggestions);
  };

  const suggestionSelected = (value) => {
    setSearchTerm(value);
    setSuggestions([]);
  };

  return (
    <div className="search-bar">
      <input
        type="text"
        value={searchTerm}
        onChange={onSearchChange}
        placeholder="Search..."
      />
      {suggestions.length > 0 && (
        <ul className="suggestions">
          {suggestions.map((suggestion, index) => (
            <li key={index} onClick={() => suggestionSelected(suggestion)}>
              {suggestion}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Nav;
