import React from "react";
import "react-image-gallery/styles/css/image-gallery.css";
import Gallery from "react-image-gallery";
import "./imageslider.css";

function ImageSlider() {
  const images = [
    {
      original: "one.jpg",
        // thumbnail: 'one.jpg',
      description: "Image 1",
    },
    {
      original: "two.jpg",
        thumbnail: 'two.jpg',
      description: "Image 2",
    },
    {
      original: "three.jpg",
        thumbnail: 'three.jpg',
      description: "Image 3",
    },
    {
      original: "four.jpg",
        thumbnail: 'four.jpg',
      description: "Image 3",
    },
  ];

  return (
    <div>
      <Gallery
        items={images}
        autoPlay={true} // Enable automatic playback
        slideInterval={5000} // Set the interval between slides in milliseconds (e.g., 5 seconds)
        infinite={true}
      // Loop back to the beginning after reaching the last slide
      />
    </div>
  );
}

export default ImageSlider;
