import "./nav.css";
import { keywords } from "./seo";
// import { useState } from "react";

function Nav() {
  document.addEventListener("DOMContentLoaded", function () {
    let wrapper = document.getElementById("search-wrapper");
    let searchinput = document.getElementById('search');
    let autocom = document.getElementById('autocom');

    async function changeinput(e) {
      const value = e.target.value;
      let usersearch = value;
      let suggest = [];

      if (usersearch) {
        suggest = await keywords.filter((data) =>
          data.toLowerCase().startsWith(usersearch.toLowerCase())
        );
        suggest = await suggest.map((data) => {
          return `<li> ${data} </li>`;
        });
        await wrapper.classList.add("active");
        await showsugg(suggest);

        let alllist = document.querySelectorAll("li");
        for (let index = 0; index < alllist.length; index++) {
          alllist[index].addEventListener("click", (e) => {
            let selected = e.target.textContent;
            console.log(selected);
          });
        }
      } else {
        wrapper.classList.remove("active");
      }
    }

    function showsugg(list) {
      let listdata;
      if (!list.length) {
        let uservalue = searchinput.value;
        listdata = `<li> ${uservalue} </li>`;
      } else {
        listdata = list.join('');
      }
      autocom.innerHTML = listdata;
    }

    searchinput.addEventListener
    // You can also call your changeinput function here or wherever it's needed.
  });

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      {/* <div className="container"> */}
      <a className="navbar-brand" href="#" id="brand">
        <img
          src="logo192.png"
          style={{ height: "30px", width: "30px" }}
          alt="React"
        />{" "}
        React
      </a>
      <button
        className="navbar-toggler"
        type="button"
        data-bs-toggle="collapse"
        data-bs-target="#navbarNav"
        aria-controls="navbarNav"
        aria-expanded="false"
        aria-label="Toggle navigation"
      >
        <span className="navbar-toggler-icon"></span>
      </button>
      <div className="collapse navbar-collapse" id="navbarNav">
        <ul className="navbar-nav">
          <li className="nav-item">
            <a
              className="nav-link active"
              id="active"
              aria-current="page"
              href="#"
            >
              Home
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              About
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              Contact
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              More
            </a>
          </li>
        </ul>
        {/* </div> */}
        {/* <div className="search-container"> */}
        <div className="search-wrapper" id="search-wrapper">
          <div className="input-group search-field">
            <input
              type="search"
              className="form-control form-control-sm"
              id="search"
              placeholder="Search any product you want!"
              // onClick={changeinput}
            />
            <div className="auto-com" id="autocom">
            </div>
          </div>
        </div>
        <button
          className="btn btn-outline-secondary bg-primary"
          type="button"
          style={{ color: "#333333" }}
          id="searchbtn"
        >
          Search
        </button>

      <div className="cta">
        <button className="btn btn-primary" id="login">
          Login
        </button>
        <button className="btn btn-success" id="signup">
          Sign Up
        </button>
      </div>
    </div>
      {/* </div> */}
      {/* </div> */}
    </nav>
  );
}

export default Nav;
