import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { categories } from "../main/categorieslist";

function Listing({selectedCategory}) {
        const { category } = useParams();
       
        // Filter products based on the selected category or show all products


  return (
    <div className="container mt-5 card p-3">
      <div className="row">
        <div className="col-lg-3 col-md-4 col-sm-12 mb-4">
          <div className="filter-sidebar">
            <h4>Filter by Category</h4>
            <select
              className="form-select"
              onChange={(e) => setSelectedCategory(e.target.value)}
              value={selectedCategory}
            >
              <option value="All">All</option>
              <option value="Electronics">Electronics</option>
              <option value="Furniture">Furniture</option>
              <option value="Fashion">Fashion</option>
            </select>
          </div>
        </div>
        <div className="col-lg-9 col-md-8 col-sm-12">
          <div className="row">
            {filterProducts().map((product) => (
              <div key={product.id} className="col-lg-4 col-md-6 col-sm-6 mb-4">
                <div className="card product-card">
                  <img
                    src={`smart tv.webp`}
                    className="card-img-top"
                    alt={product.name}
                  />
                  <div className="card-body">
                    <h5 className="card-title">{product.name}</h5>
                    <p className="card-text">${product.price}</p>
                    <p className="card-text">
                      <small className="text-muted">
                        Discount: ${product.discount}
                      </small>
                    </p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Listing;
