import { items } from "./seo";
// import React from "react";
import "./nav.css";
import React from "react";
import { ReactSearchAutocomplete } from "react-search-autocomplete";
import { useState } from "react";
import LoginForm from "./login";

function Nav() {
  const[displayLogin, setDisplayLogin] = useState('none')
  function handleClickOnlogin(){
    setDisplayLogin("block")
  }
  const handleOnSearch = (string, results) => {
    // onSearch will have as the first callback parameter
    // the string searched and htmlFor the second the results.
    results.slice(1, 5);
    console.log(string, results);
  };

  const handleOnHover = (result) => {
    // the item hovered
    console.log(result);
  };

  const handleOnSelect = (item) => {
    // the item selected
    console.log(item);
  };

  const handleOnFocus = () => {
    console.log("Focused");
  };

  const htmlFormatResult = (item) => {
    return (
      <>
        {/* <span style={{ display: 'block', textAlign: 'left' }}>id: {item.id}</span> */}
        <span
          style={{ display: "block", textAlign: "left", visibility: "visible" }}
        >
          {item.name}
        </span>
      </>
    );
  };
  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      {/* <div className="container"> */}
      <a className="navbar-brand" href="#" id="brand">
        <img
          src="logo192.png"
          style={{ height: "30px", width: "30px" }}
          alt="React"
        />{" "}
        React
      </a>
      <button
        className="navbar-toggler"
        type="button"
        data-bs-toggle="collapse"
        data-bs-target="#navbarNav"
        aria-controls="navbarNav"
        aria-expanded="false"
        aria-label="Toggle navigation"
      >
        <span className="navbar-toggler-icon"></span>
      </button>
      <div className="collapse navbar-collapse" id="navbarNav">
        <ul className="navbar-nav">
          <li className="nav-item">
            <a
              className="nav-link active"
              id="active"
              aria-current="page"
              href="#"
            >
              Home
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              About
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              Contact
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#">
              More
            </a>
          </li>
        </ul>
        <ReactSearchAutocomplete
          items={items}
          onSearch={handleOnSearch}
          onHover={handleOnHover}
          onSelect={handleOnSelect}
          onFocus={handleOnFocus}
          autoFocus
          htmlFormatResult={htmlFormatResult}
        />

        <div className="cta">
          <button
            className="btn"
            style={{ color: "#333333", background: "#f2f2f2" }}
            id="login"
            onClick={handleClickOnlogin}
          >
            Login
          </button>
          <button className="btn btn-primary" id="signup">
            Sign Up
          </button>
        </div>
      </div>
      <LoginForm displayLogin={displayLogin} />
    </nav>
  );
}

export default Nav;
