import React from 'react';
import Nav from './navBar/nav';
import ImageSlider from './main/Slider';
import { Category } from './main/categories';
import { useState } from 'react';

export default function App() {
    const [blur, setblur] = useState(false);
    const [displayLogin, setDisplayLogin] = useState('none')
    const [displaySignup, setDisplaySignup] = useState('none')

    function handleClickOnLogin() {
        if (displayLogin === "none")
         setDisplayLogin("block");
        if (displayLogin === "block") {
          setDisplayLogin("none");
        }
        setDisplaySignup("none")
        setblur(true)
      }

      function handleClickOnSignup(){
        setDisplaySignup(displaySignup === "none"? "block" : "none")
        setDisplayLogin("none")
        setblur(true)
      }
    return(
        <>
        <Nav displayLogin={displayLogin} displaySignup={displaySignup} handleClickOnLogin={handleClickOnLogin} handleClickOnSignup={handleClickOnSignup} />
        <ImageSlider className={dis? 'blur' : ''}/> 
        <Category />   
        </>
    )
}
